import { ArrowBackIosOutlined, ArrowForwardIosOutlined } from '@material-ui/icons'
import React, { useRef, useState } from 'react'
import ListItem from '../ListItem/ListItem'
import './List.scss'

function List() {
    const [isMoved, setIsMoved] = useState(false);
    const [slideNumber, setSlideNuber] = useState(0);

    const listRef = useRef();
    let distance;
    const handleClick = direction => {
        setIsMoved(true);
        distance = listRef.current.getBoundingClientRect().x - 50;
        if(direction === "left" && slideNumber > 0){
            setSlideNuber(prevState => prevState - 1);
            listRef.current.style.transform = `translateX(${260 + distance}px)`
        } if(direction === "right" && slideNumber < 3) {
            setSlideNuber(prevState => prevState + 1);
            listRef.current.style.transform = `translateX(${-260 + distance}px)`
        }
        console.log(distance)
    }

    return (
        <div className="list">
           <span className="listTitle">Continue to watch</span>
           <div className="wrapper">
                <ArrowBackIosOutlined onClick={() => handleClick("left")} style={{display: !isMoved && distance === 0 && "none"}} className="arrow left"/>
                <div className="container" ref={listRef}>
                    <ListItem />
                    <ListItem />
                    <ListItem />
                    <ListItem />
                    <ListItem />
                    <ListItem />
                    <ListItem />
                    <ListItem />
                    <ListItem />
                    <ListItem />
                </div>
                <ArrowForwardIosOutlined onClick={() => handleClick("right")} className="arrow right"/>
           </div>
        </div>
    )
}

export default List
