import React, { useRef, useState } from 'react'
import {Link} from 'react-router-dom'
import './Register.scss'

function Register() {

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const emailRef = useRef();
    const passwordRef = useRef();

    const handleStart = () => {
        setEmail(emailRef.current.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        setPassword(passwordRef.current.value)
    }

    return (
        <div className="register">
            <div className="top">
                <div className="wrapper">
                    <img className="logo" src="http://assets.stickpng.com/images/580b57fcd9996e24bc43c529.png" alt="" />
                    <Link to="/login"> 
                    <button className="loginButton">Sign In</button>
                    </Link>
                </div>
            </div>
            <div className="container">
                <h1>Lorem ipsum dolor sit amet.</h1>
                <h2>Lorem ipsum dolor sit amet consectetur, adipisicing elit. Cupiditate, molestiae.</h2>
                <p>Lorem ipsum, dolor sit amet consectetur adipisicing.</p>
                {!email && (
                    <div className="input">
                        <input ref={emailRef} type="email" placeholder="Enter your email address"/>
                        <button onClick={handleStart} className="registerButton">Get Started</button>
                    </div>
                )}
                {email && (
                    <form onSubmit={handleSubmit} className="input">
                        <input ref={passwordRef} type="password" placeholder="Enter your password"/>
                        <button className="registerButton">Start</button>
                    </form>
                )}

            </div>
        </div>
    )
}

export default Register
