import React, { useState, useContext, useEffect } from "react";
import "./Navbar.scss";
import { Search, Notifications, ArrowDropDown } from "@material-ui/icons";
import { Link } from "react-router-dom";
import { AuthContext } from "../../context/auth/AuthContext";

function Navbar() {
  const { isLoggedIn, logout } = useContext(AuthContext);
  const [isScrolled, setIsScrolled] = useState(false);
  const [search, setSearch] = useState("");
  const [isShow, setIsShow] = useState(false);
  const [isBlur, setIsBlur] = useState(false);

  window.onscroll = () => {
    setIsScrolled(window.pageYOffset === 0 ? false : true);
    return () => {
      window.onscroll = null;
    };
  };

  useEffect(() => {
    if (search.length !== 0) {
      setIsShow(true);
    } else {
      setIsShow(false);
    }
  }, [search]);

  console.log(isBlur);

  return (
    <>
      <div className={isScrolled ? "navbar scrolled" : "navbar"}>
        <div className="container">
          <div className="left">
            <img
              src="http://assets.stickpng.com/images/580b57fcd9996e24bc43c529.png"
              alt=""
            />
            <Link className="link" to="/">
              <span>Homepage</span>
            </Link>
            <Link className="link" to="/series">
              <span>Series</span>
            </Link>
            <Link className="link" to="/movies">
              <span>Movies</span>
            </Link>
            <span>New and Popular</span>
            <span>My list</span>
          </div>
          <div className="right">
            <div className="search-box">
              <button className="btn-search">
                <Search className="icon" />
              </button>
              <input
                type="text"
                onBlur={() => setIsBlur(!isBlur)}
                onChange={(e) => setSearch(e.target.value)}
                className="input-search"
                placeholder="Type to Search..."
              />
              {isShow && isBlur && (
                <div
                  style={isShow ? { display: "block" } : { display: "none" }}
                  className="movieSearchContainer"
                >
                  <div className="item-movie">
                    <div className="img_movieContainer">
                      <img
                        className="img_movie"
                        src="https://images.pexels.com/photos/8694231/pexels-photo-8694231.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
                        alt=""
                      />
                    </div>
                    <div className="movie-info">
                      {isBlur && <span>asdasd</span>}
                      <span>
                        Title <span>(2012)</span>
                      </span>
                      <span className="movieDesc">
                        Lorem ipsum dolor sit amet consectetur adipisicing elit.
                        Iusto, quasi.
                      </span>
                    </div>
                  </div>
                  <hr />
                  <div className="item-movie">
                    <div className="img_movieContainer">
                      <img
                        className="img_movie"
                        src="https://images.pexels.com/photos/8694231/pexels-photo-8694231.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
                        alt=""
                      />
                    </div>
                    <div className="movie-info">
                      <span>
                        Title <span>(2012)</span>
                      </span>
                      <span className="movieDesc">
                        Lorem ipsum dolor sit amet consectetur adipisicing elit.
                        Iusto, quasi.
                      </span>
                    </div>
                  </div>
                  <hr />
                </div>
              )}
            </div>

            {isLoggedIn && (
              <>
                <span>KID</span>
                <div className="iconNoti">
                  <Notifications className="icon" />
                  <span className="iconBadge"></span>
                </div>
                <img
                  src="https://eitrawmaterials.eu/wp-content/uploads/2016/09/person-icon.png"
                  alt=""
                />
                <div className="profile">
                  <ArrowDropDown className="icon" />
                  <div className="option">
                    <span>Settings</span>
                    <span onClick={() => logout()}>Logout</span>
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    </>
  );
}

export default Navbar;
