import React, { useState, useContext, useRef } from "react";
import { Link } from "react-router-dom";
import "./Register.scss";
import HttpHook from "../../hooks/HttpHook";
import { AuthContext } from "../../context/auth/AuthContext";

function Register() {
  const [user, setUser] = useState(null);
  const emailRef = useRef("");
  const { sendRequest } = HttpHook();
  const { token } = useContext(AuthContext);
  const DS = process.env.REACT_APP_DOMAIN_SERVER;
  const [email, setEmail] = useState("");
  const handlerChange = () => {
    setUser({ ...user, email });
  };

  const handleEmail = () => {
    setUser({ ...user, email : emailRef.current.value });
  }

  const handleSubmit = async (e) => {
    e.preventDefault();

    await sendRequest(`${DS}/auth/register`, "POST", JSON.stringify(user), {
      "Content-Type": "application/json",
      Authorization: "Bearer " + token,
    });
  };

  console.log(user);

  return (
    <div className="register">
      <div className="top">
        <div className="wrapper">
          <img
            className="logo"
            src="http://assets.stickpng.com/images/580b57fcd9996e24bc43c529.png"
            alt=""
          />
          <Link to="/login">
            <button className="loginButton">Sign In</button>
          </Link>
        </div>
      </div>
      <div className="container">
        <h1>Lorem ipsum dolor sit amet.</h1>
        <h2>
          Lorem ipsum dolor sit amet consectetur, adipisicing elit. Cupiditate,
          molestiae.
        </h2>
        <p>Lorem ipsum, dolor sit amet consectetur adipisicing.</p>
        {!email && (
          <div className="input">
            <input
              type="email"
              ref={emailRef}
              placeholder="Enter your email address"
              onClick={handleEmail}
            />
            <button className="registerButton">Get Started</button>
          </div>
        )}
        {email && (
          <form onSubmit={handleSubmit} className="input">
            <input
              onChange={handlerChange}
              type="password"
              placeholder="Enter your password"
            />
            <input
              onChange={handlerChange}
              type="password"
              placeholder="Enter your password"
            />
            <button className="registerButton">Start</button>
          </form>
        )}
      </div>
    </div>
  );
}

export default Register;
