import { InfoOutlined, PlayArrow } from "@material-ui/icons";
import React, { useEffect, useState } from "react";
import "./Featured.scss";
import HttpHook from "../../hooks/HttpHook";

function Featured({ type }) {
  const [content, setContent] = useState();
  const { sendRequest } = HttpHook();
  const DS = process.env.REACT_APP_DOMAIN_SERVER;

  useEffect(() => {
    const getRandomContent = async () => {
      const response = await sendRequest(
        `${DS}/movie/random?type=${type}`,
        "GET",
        null,
        {
          Authorization:
            "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySWQiOiI2MGY0MzIyMjkxMzM3NzVjMzUxZTBlNzUiLCJpc0FkbWluIjp0cnVlLCJpYXQiOjE2MjY2ODc1NTAsImV4cCI6MTYyNjY5NDc1MH0.up-ykFgwo8TivthevO7Ib-Fvz1U_OFLyq7DODWlnecY",
        }
      );

      setContent(response.movie);
    };
    getRandomContent();
  }, [DS, sendRequest, type]);
  console.log(content)
  return (
    <div className="featured">
      {type && (
        <div className="category">
          <span>{type === "movie" ? "Movies" : "Series"}</span>
          <select name="genre" id="genre">
            <option>Genre</option>
            <option>Genre</option>
            <option>Genre</option>
            <option>Genre</option>
            <option>Genre</option>
            <option>Genre</option>
            <option>Genre</option>
            <option>Genre</option>
            <option>Genre</option>
            <option>Genre</option>
            <option>Genre</option>
            <option>Genre</option>
            <option>Genre</option>
            <option>Genre</option>
            <option>Genre</option>
            <option>Genre</option>
          </select>
        </div>
      )}
      <img src={content.img} alt="" />
      <div className="info">
        <h1 className="title">CALIBRE</h1>
        <span className="desc">{content.desc}</span>
        <div className="buttons">
          <button className="play">
            <PlayArrow />
            <span>Play</span>
          </button>
          <button className="more">
            <InfoOutlined />
            <span>Info</span>
          </button>
        </div>
      </div>
    </div>
  );
}

export default Featured;
