import React, { useEffect, useState } from "react";
import Featured from "../../components/Featured/Featured";
import List from "../../components/List/List";
import Navbar from "../../components/Navbar/Navbar";
import "./Home.scss";
import HttpHook from "../../hooks/HttpHook";

function Home(props) {
  const [lists, setLists] = useState([]);
  const [genre, setGenre] = useState(null);
  const { sendRequest } = HttpHook();
  const DS = process.env.REACT_APP_DOMAIN_SERVER;

  useEffect(() => {
    const getRandomLists = async () => {
      const response = await sendRequest(
        `${DS}/list${props.type ? "?type=" + props.type : ""}${
          genre ? "&genre=" + genre : ""
        }`,
        "GET",
        null,
        {
          Authorization:
            "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySWQiOiI2MGY0MzIyMjkxMzM3NzVjMzUxZTBlNzUiLCJpc0FkbWluIjp0cnVlLCJpYXQiOjE2MjY2ODc1NTAsImV4cCI6MTYyNjY5NDc1MH0.up-ykFgwo8TivthevO7Ib-Fvz1U_OFLyq7DODWlnecY",
        }
      );

      setLists(response.list);
    };
    getRandomLists();
  }, [DS, sendRequest, props.type, genre]);

  return (
    <div className="home">
      <Navbar />
      <Featured type={props.type} />
      {lists.map((list) => (
        <List key={list._id} list={list} />
      ))}
    </div>
  );
}

export default Home;
