import {
  Add,
  PlayArrow,
  ThumbDownAltOutlined,
  ThumbUpAltOutlined,
} from "@material-ui/icons";
import React, { useEffect, useState } from "react";
import "./ListItem.scss";
import HttpHook from "../../hooks/HttpHook";
// import trailer from '../../../public/assets/trailer.mp4'

function ListItem({ index, item }) {
  const [isHoverd, setIsHovered] = useState(false);
  const { sendRequest } = HttpHook();
  const [movie, setMovie] = useState({});
  const DS = process.env.REACT_APP_DOMAIN_SERVER;

  useEffect(() => {
    const getRandomLists = async () => {
      const response = await sendRequest(
        `${DS}/movie/find/${item._id}`,
        "GET",
        null,
        {
          Authorization:
            "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySWQiOiI2MGY0MzIyMjkxMzM3NzVjMzUxZTBlNzUiLCJpc0FkbWluIjp0cnVlLCJpYXQiOjE2MjY2ODc1NTAsImV4cCI6MTYyNjY5NDc1MH0.up-ykFgwo8TivthevO7Ib-Fvz1U_OFLyq7DODWlnecY",
        }
      );

      console.log(response);
    };
    getRandomLists();
  }, [DS, sendRequest, item._id]);
  return (
    <div
      className="listItem"
      style={{ left: isHoverd && index * 255 - 50 + index * 2.5 }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <img src={item.img} alt="" />
      {isHoverd && (
        <>
          <video src={item.trailer} autoPlay={true} loop></video>
          <div className="itemInfo">
            <div className="icons">
              <PlayArrow className="icon play" />
              <Add className="icon add" />
              <ThumbUpAltOutlined className="icon like" />
              <ThumbDownAltOutlined className="icon dislike" />
            </div>
            <div className="itemInfoTop">
              <span>{item.duration}</span>
              <span className="limitAge">{item.limit}</span>
              <span>{item.year}</span>
            </div>
            <div className="desc">{item.desc}</div>
            <div className="genre">{item.genre}</div>
          </div>
        </>
      )}
    </div>
  );
}
export default ListItem;
